mapper.select:
select com_id, com_name, com_pwd, com_num, com_tel1, com_tel2, com_tel3, com_mobile1, com_mobile2, com_mobile3, com_fax1, com_fax2, com_fax3, com_address, com_ceo, com_email1, com_email2, com_regdate, com_logo, com_outdate from ( select rownum as RNUM ,LIST.* from ( select * from company
order by com_regdate )LIST )
where RNUM>#{firstRecordIndex} and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
