mapper.select:
select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from company 
        
        order by com_regdate 
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from company 
        
        order by com_regdate 
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
and com_outdate is null

select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from company 
        
        order by com_regdate
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
and com_outdate is not null

mapper.update:
update company
set com_outdate=sysdate
where com_id=#{comId}

mapper.select:
select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select*
        from car c join CompanyCarOption cco
        on c.car_code=cco.car_code
        where com_id=#{comId}
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update company
set com_outdate=null
where com_id=#{comId}
