mapper.select:
select *
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from RENT_ADMIN
        
        order by admin_authcode desc
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update RENT_ADMIN
set admin_tel1=#{adminTel1}, admin_tel2=#{adminTel2}, admin_tel3=#{adminTel3}, 
admin_email1=#{adminEmail1}, admin_email2=#{adminEmail2}, admin_authcode=#{adminAuthcode}  
where admin_id=#{adminId}

update RENT_ADMIN
set admin_outdate=sysdate
where admin_id=#{adminId}
