mapper.select:
select*
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from RENT_USER 
        
        order by user_regdate
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select*
from
(
    select rownum as RNUM ,LIST.*
    from
    (
        select * from RENT_USER 
        
        order by user_regdate
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
and user_outdate is null

