mapper.update:
update companycaroption
set	comcar_outdate = sysdate
where ccar_car_id=#{ccarCarId} and com_id=#{comId}

update companyCarOption
set CCAR_USE_YN=#{ccarUseYn}
where ccar_car_id=#{ccarCarId} and com_id=#{comId}

mapper.select:
 select *
	from
	(
	    select rownum as RNUM ,LIST.*
	    from
	    (
	        select * from com_reservView
	        where com_id= #{companyId}
	        
	        
				
					and reserv_start_date >= to_char(sysdate,'yyyy/MM/dd')
				
				
					 and pay_condition = 'paid'
				
				
					 order by remainingday desc
				
				
				
				
			
			
				order by reserv_date desc
			
	    )LIST
	)
	
	where RNUM>#{firstRecordIndex} 
	and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM ,LIST.* from ( select * from com_reservview
where reserv_start_date <= sysdate and sysdate <=reserv_end_date
and com_id=#{companyId}
order by ccar_car_id desc
order by res_drv_name desc
order by res_drv_name desc
order by reserv_end_date
)LIST )
where RNUM>#{firstRecordIndex} and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
