mapper.select:
select*
from
(
    select rownum as RNUM ,LIST.*
    from
    (
       
		select a.*, b.COM_NAME
		from (select comc.*, c.car_name, c.car_inc, c.car_size, c.car_trans, car_type, car_img
		        from (select c.*, 
		        (to_date(#{combinedSearchEndDate}, 'yyyy-mm-dd hh24:mi')-to_date(#{combinedSearchStartDate}, 'yyyy-mm-dd hh24:mi')) * CCAR_NORMAL_PRICE
		        as "PRICERESULT"  
		        from COMPANYCAROPTION c
		        where car_code in (
		        select car_code from car
		        
		        	where car_type=#{carType}
						        
		        )
		        and ccar_car_id not in (
		        select ccar_car_id from RESERVATION
		        where reserv_end_date >= TO_DATE(#{combinedSearchStartDate},'yyyy-mm-dd hh24:mi') 
				and reserv_start_date <= TO_DATE(#{combinedSearchEndDate},'yyyy-mm-dd hh24:mi')
		        )
		        and comcar_outdate is null
		    ) comc join car c
		    on comc.car_code=c.car_code) a join company b
		on a.com_id=b.com_id
		order by a.car_name
		
    )LIST
)

where RNUM>#{firstRecordIndex} 
and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}

select count(*) from (
select a.*, b.COM_NAME from (select comc.*, c.car_name, c.car_inc, c.car_size, c.car_trans, car_type, car_img from (select c.*, (to_date(#{combinedSearchEndDate}, 'yyyy-mm-dd hh24:mi')-to_date(#{combinedSearchStartDate}, 'yyyy-mm-dd hh24:mi')) * CCAR_NORMAL_PRICE as "PRICERESULT" from COMPANYCAROPTION c where car_code in ( select car_code from car
where car_type=#{carType}
) and ccar_car_id not in ( select ccar_car_id from RESERVATION where reserv_end_date >= TO_DATE(#{combinedSearchStartDate},'yyyy-mm-dd hh24:mi') and reserv_start_date <= TO_DATE(#{combinedSearchEndDate},'yyyy-mm-dd hh24:mi') ) and comcar_outdate is null ) comc join car c on comc.car_code=c.car_code) a join company b on a.com_id=b.com_id order by a.car_name
)
